import React, { useState } from "react";

const Navbar = () => {
  const [isDropdownOpen, setDropdownOpen] = useState(false);

  const handleDropdownToggle = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  return (
    <>
      
        <nav className="border-gray-200">
          <div className="container mx-auto flex flex-wrap items-center justify-between">
            <a href="/settings" className="flex">
              
              <span className="self-center text-lg font-semibold whitespace-nowrap">
                Alkane
              </span>
            </a>
            <button
              data-collapse-toggle="mobile-menu"
              type="button"
              className="md:hidden ml-3 text-gray-400 hover:text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-300 rounded-lg inline-flex items-center justify-center"
              aria-controls="mobile-menu-2"
              aria-expanded="false"
            >
              {/* Mobile Menu Icon */}
            </button>
            <div
              className="hidden md:block w-full md:w-auto"
              id="mobile-menu"
            >
              <ul className="flex-col md:flex-row flex md:space-x-8 mt-4 md:mt-0 md:text-sm md:font-medium">
                <li>
                  <a
                    href="/"
                    className="bg-blue-700 md:bg-transparent text-white block pl-3 pr-4 py-2 md:text-blue-700 md:p-0 rounded focus:outline-none"
                    aria-current="page"
                  >
                    Home
                  </a>
                </li>
                <li>
                  <button
                    id="dropdownNavbarLink"
                    onClick={handleDropdownToggle}
                    data-dropdown-toggle="dropdownNavbar"
                    className="text-gray-700 hover:bg-gray-50 border-b border-gray-100 md:hover:bg-transparent md:border-0 pl-3 pr-4 py-2 md:hover:text-blue-700 md:p-0 font-medium flex items-center justify-between w-full md:w-auto"
                  >
                    Dropdown{" "}
                    
                  </button>
                  {/* <!-- Dropdown menu --> */}
                  <div
                    id="dropdownNavbar"
                    className={`absolute bg-white text-base z-10 list-none divide-y divide-gray-100 rounded shadow my-4 w-44 ${
                      isDropdownOpen ? "block" : "hidden"
                    }`}
                  >
                    <ul
                      className="py-1"
                      aria-labelledby="dropdownNavbarLink"
                    >
                      <li>
                        <a
                          href="/settings"
                          className="text-sm hover:bg-gray-100 text-gray-700 block px-4 py-2"
                        >
                          Dashboard
                        </a>
                      </li>
                      <li>
                        <a
                          href="/profile"
                          className="text-sm hover:bg-gray-100 text-gray-700 block px-4 py-2"
                        >
                          Profile
                        </a>
                      </li>
                      <li>
                        <a
                          href="/settings"
                          className="text-sm hover:bg-gray-100 text-gray-700 block px-4 py-2"
                        >
                          Earnings
                        </a>
                      </li>
                    </ul>
                    <div className="py-1">
						<button onClick={handleLogout} className="text-sm hover:bg-gray-100 text-gray-700 block px-4 py-2">
                      
                       
                        
                     
                        Sign out
                      
					  </button>
                    </div>
                  </div>
                </li>
              </ul>
            </div>
          </div>
        </nav>

       
     
    </>
  );
};

export default Navbar;


